INSERT INTO snet1509.comments (user_id,post_id,body,created_at,updated_at) VALUES
	 (237,1747,'Кирила Петрович обратился к скверному мальчику и  спросил  его  грозно:','2020-10-02 21:58:09.0','2020-10-02 21:58:09.0'),
