INSERT INTO snet1509.comments (user_id,post_id,body,created_at,updated_at) VALUES
	 (1007,12,'- Молчи, Александр, - отвечал Кирила  Петрович,  -  не  забудь,  что  я','2020-10-02 21:58:09.0','2020-10-02 21:58:09.0'),
	 (791,1714,'собираюсь с тобою разделаться. Ступай в свою  комнату.  Ты,  косой,  ты  мне','2020-10-02 21:58:09.0','2020-10-02 21:58:09.0'),
