INSERT INTO snet1509.comments (user_id,post_id,body,created_at,updated_at) VALUES
	 (95,394,'Между тем тележка въехала на двор, и знакомый уже нам исправник вошел в','2020-10-02 21:58:09.0','2020-10-02 21:58:09.0'),
