INSERT INTO snet1509.comments (user_id,post_id,body,created_at,updated_at) VALUES
	 (875,504,'моего; толпа разбойников, бежавшая  от  неожиданной  нашей  вылазки,  тотчас','2020-10-02 21:58:09.0','2020-10-02 21:58:09.0'),
