INSERT INTO snet1509.posts (user_id,body,metadata,created_at,updated_at) VALUES
	 (93,'Мария сказала: се раба Господня; да будет мне по слову твоему. И  отошел  отнее ангел" (Евангелие от Луки, гл. 1, стихи 26-38).О грехопадении Адама и Евы в  библии  рассказано  так:  "Змей  {4}  былхитрее всех зверей полевых, которых создал господь бог. И сказал змей  жене:подлинно ли сказал бог: не ешьте ни от какого дерева в раю? И  сказала  женазмею: плоды с дерев мы можем есть; только плодов дерева, которое среди сада,сказал бог, не ешьте их и не прикасайтесь к ним, чтобы  вам  не  умереть.  Исказал змей жене: нет, не умрете; но знает бог, что в  день,  в  который  вывкусите их, то откроются глаза ваши, и вы будете как боги, знающие  добро  и',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
	 (94,'Враждебный меч не перерубит,Никто из витязей лихих,Никто из смертных не погубитМалейших замыслов моих;Моею будет век Людмила,Руслан же гробу обречен!"И мрачно ведьма повторила:"Погибнет он! погибнет он!"Потом три раза прошипела,',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
	 (94,'Уж и впрямь была царица:Высока, стройна, бела,И умом и всем взяла;Но зато горда, ломлива,Своенравна и ревнива.Ей в приданое даноБыло зеркальце одно;Свойство зеркальце имело:Говорить оно умело.',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
	 (94,'Да встретит юная супругаТебя в веселье и слезах,Да выпьешь мед из чаши друга,А недруга низринешь в прах".Потом со скал он к челну сходитИ влажный узел развязал.Надулся парус, побежал.Но старец долго глаз не сводитС крутых прибрежистых вершин,',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
