INSERT INTO snet1509.posts (user_id,body,metadata,created_at,updated_at) VALUES
	 (89,'О чем вздыхаешь ты всечасно?Здесь люди вольны, небо ясно,И жены славятся красой.Не плачь: тоска тебя погубит.АлекоОтец, она меня не любит.',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
	 (195,'Утешься, друг: она дитя.Твое унынье безрассудно:Ты любишь горестно и трудно,А сердце женское - шутя.Взгляни: под отдаленным сводомГуляет вольная луна;На всю природу мимоходомРавно сиянье льет она.',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
	 (115,'Его так пышно озарит -И вот - уж перешла в другое;И то недолго посетит.Кто место в небе ей укажет,Примолвя: там остановись!Кто сердцу юной девы скажет:Люби одно, не изменись?Утешься.',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
	 (161,'Как она любила!Как нежно преклонясь ко мне,Она в пустынной тишинеЧасы ночные проводила!Веселья детского полна,Как часто милым лепетаньемИль упоительным лобзаньемМою задумчивость она',NULL,'2020-10-02 21:38:54.0','2020-10-02 21:38:54.0'),
